            Eigenaar_1: "5AEF734B-A1B6-4901-AEDB-EA2743F5F817",
            Eigenaar_2: "FF78CCA8-D94D-4179-A0C4-478428D67E00",
            Portefeuillehouder_1: "8DD9BDB0-C2B7-40D3-825D-E78236606D58",
